import { useEffect, useState } from "react";
import Base from "../Base";
import { fetchAllArticlesOfUser } from "./helper/articlehelper";


const Home = () => {
    const [articles, setArticles] = useState([])

    useEffect(() => {
        fetchAllArticlesOfUser().then(async articles => {
            setArticles(await articles.json())
            console.log(articles);
        }).catch(err => console.log(err))
    }, [])
    return (
        <Base title="Articles" description="All the articles">
            <div className="row">

                {articles && articles.map((article, index) => {
                    return (
                        <div key={index} onClick={() => {
                            // window.location.href = article.article_link 
                            window.open(article.article_link, '_blank').focus();

                        }} className="card  my-1 text-white bg-dark border border-info">
                            <div className="card-body">
                                <h3 className="text-white text-center">{article.article_title}</h3>
                            </div>
                        </div>
                    );
                })}
            </div>

        </Base>
    )
}

export default Home;
